      <ul>
        {assignments.map((a, idx) => (
          <li key={idx} className="mb-1">
            <strong>{a.course}:</strong> {a.name} â€” due {a.due_at ? new Date(a.due_at).toLocaleString() : "N/A"}
          </li>
        ))}
      </ul>
    </div>
  );
}
